CREATE TABLE IF NOT EXISTS tCharacter (
  id       integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  SiId     int NOT NULL,
  Charact  varchar(255),
  Value    varchar(255)
);

-- Table: tContacts
CREATE TABLE tContacts (
  id           integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  SiId         int NOT NULL,
  Name         varchar(255),
  Org          varchar(255),
  Addr         varchar(255),
  Description  varchar(255)
);

-- Table: tDepartments
CREATE TABLE tDepartments (
  id integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  department  varchar(255) NOT NULL
);

-- Table: tMeasureCodes
CREATE TABLE tMeasureCodes (
  id    integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  Name  varchar(255) NOT NULL
);

-- Table: tPackage
CREATE TABLE tPackage (
  id     integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  SiId   integer NOT NULL,
  Name   varchar(255),
  Count  varchar(255)
);

-- Table: tPlacement
CREATE TABLE tPlacement (
  id         integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  Placement  varchar(255) NOT NULL
);

-- Table: tPov
CREATE TABLE tPov (
  id       integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  SiId     integer NOT NULL,
  PovDate  date NOT NULL,
  Scan     blob,
  Doc      varchar(255)
);

-- Table: tResposible
CREATE TABLE tResposible (
  id          integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  RespPerson  varchar(255) NOT NULL
);

-- Table: tService
CREATE TABLE tService (id integer PRIMARY KEY AUTO_INCREMENT NOT NULL, SiId int NOT NULL, ServDate date, ServType integer REFERENCES tServTypes (id) ON UPDATE CASCADE, Executor varchar(255), Description varchar(255), NextDate DATE, FOREIGN KEY (SiId) REFERENCES tSI (SiId) ON DELETE NO ACTION ON UPDATE CASCADE);

-- Table: tServTypes
CREATE TABLE tServTypes (
  id      integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  Name    varchar(255),
  Period  integer
);

-- Table: tSI
CREATE TABLE tSI (SiId integer PRIMARY KEY AUTO_INCREMENT NOT NULL, Name varchar(255) NOT NULL, SN varchar(255) NOT NULL, RespPerson integer REFERENCES tResposible (id) ON UPDATE CASCADE, Department integer REFERENCES tDepartments (id) ON UPDATE CASCADE, Placement integer REFERENCES tPlacement (id) ON UPDATE CASCADE, ManufactDate date, InvNum varchar(255), DevCode varchar(255), MeasureCode integer REFERENCES tMeasureCodes (id) ON UPDATE CASCADE, Status integer REFERENCES tStatus (id) ON UPDATE CASCADE, IsMeasure BOOLEAN, PovPeriod integer, min varchar(255) (50), max varchar(255) (50), error varchar(255), unit varchar(255), Type integer REFERENCES tTypes (id) ON UPDATE CASCADE, nWorkPlace varchar(255));

-- Table: tStatus
CREATE TABLE tStatus (id integer PRIMARY KEY AUTO_INCREMENT NOT NULL, Status varchar(255) NOT NULL);

-- Table: tTypes
CREATE TABLE tTypes (
  id    integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  Type  varchar(255)
);

-- Table: tWorkLog
CREATE TABLE tWorkLog (id INTEGER PRIMARY KEY AUTO_INCREMENT, SiId INTEGER REFERENCES tSI (SiId) ON DELETE NO ACTION ON UPDATE NO ACTION, Name varchar(255) (255), Description varchar(255) (255), Date DATETIME);

-- Table: tWorkPlaces
CREATE TABLE tWorkPlaces (
  id         integer PRIMARY KEY AUTO_INCREMENT NOT NULL,
  Workplace  integer NOT NULL
);
