view.ddl:
CREATE VIEW vSticker AS SELECT SI.Name AS Name, SI.SN AS SN, p.PovDate AS PovDate, t.Type AS Type, s.status AS status, r.RespPerson AS RespPerson, -- strftime('%d%m%Y',strftime('%Y-%m-%d',p.PovDate)+strftime('%m',SI.PovPeriod)) date(p.PovDate,'+'||SI.PovPeriod||' month') FROM tSI as SI LEFT JOIN tTypes as t ON t.id=SI.Type LEFT JOIN tStatus as s ON s.id=SI.Status LEFT JOIN tResposible as r ON r.id=SI.RespPerson LEFT JOIN tPov as p ON p.SiId=SI.SiId
ORDER BY SI.Department DESC
-- date(tPov.PovDate,'+'+tSI.PovPeriod+'day');

view.select:
SELECT SI.Name AS Name, SI.SN AS SN, p.PovDate AS PovDate, t.Type AS Type, s.status AS status, r.RespPerson AS RespPerson, -- strftime('%d%m%Y',strftime('%Y-%m-%d',p.PovDate)+strftime('%m',SI.PovPeriod)) date(p.PovDate,'+'||SI.PovPeriod||' month') FROM tSI as SI LEFT JOIN tTypes as t ON t.id=SI.Type LEFT JOIN tStatus as s ON s.id=SI.Status LEFT JOIN tResposible as r ON r.id=SI.RespPerson LEFT JOIN tPov as p ON p.SiId=SI.SiId
ORDER BY SI.Department DESC
-- date(tPov.PovDate,'+'+tSI.PovPeriod+'day');
